var searchForm = N(".searchForm").add();
var masterGrid = N([]).grid(".masterGrid");
searchForm.data(false).comm("getSampleList.json")
	.submit(function(data) {
	masterGrid.bind(data);
});

jQuery 로 요소를 선택할 때는 반드시 view 에서 find 하거나
view를 context 로 지정 해 줘야 합니다.
N(".pageId").cont({
	init: function(view, request) {
		$("selector", view);
		view.find("selector");
	}
});
